    //   setDataterrain(response.data);
    //   console.log(dataterrain.nom_plante);
    // })
    // .catch(error => {
    //   console.log('Erreur lors de la récupération des plantes non valides :', error);
    // });


  }, []);

  return (
    <div className="list-info-cat">
      {categories.data?.map(categorie => (
        <div key={categorie.id} className="categorie-item">
          <div className="plant-image-cat">
            <img src="/assets/ad2.jpg" alt="Plant" />
          </div>
          <div className="cat-name">{categorie.name}</div>
          <div className="info-cat">Id catégorie: {categorie.id}</div>
          <DetailsTerrain pathto="/detailcategorie"/>
        </div>
      ))}
    </div>
  );

};

export default ListCategorie;
