import React from "react";
import "./index.css"; // Importez votre fichier CSS
import Input from "../elements/input/Input.js";
import BtnprimaryComponent from "../elements/button/BtnPrimary.js";
import Select from "../elements/input/Select.js"

const FormPlante = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {

    let request2 = "https://cropfarmback-production.up.railway.app/api/categorie/cultures";

    axios.get(request2)
    .then(response => {
      // Mettez à jour l'état avec les données récupérées depuis l'API
      setCategories(response.data);
    })
    .catch(error => {
      console.log('Erreur lors de la récupération des terrains non valides :', error);
    });


  }, []);
  return (
    <>
    <div className="float-bloc-list">
        <div className="greet" >
            <div className="title">Insérer une nouvelle Plante</div>
            <div className="subtitle">Soyez inspirés donnez nous un nom!</div>
        </div>
        <Input type="text" placeholder="Nom"/>
        <Input type="number" placeholder="Prix de vente"/>
        <Input type="number" placeholder="Prix d'achat"/>
        <Select name="idcategorie" obj="Catégorie de la plante"/>
        <Input type="file" placeholder="Sprite de la plante" />
        <BtnprimaryComponent holder="Créer"/>
    </div>

    </>
  );
};

export default FormPlante;
