import React, { useEffect, useState } from "react";
import "./index.css";
import Card_one from "../../card/card-1/Card_one";
import Card_two from "../../card/card-2/Card_two";
import Graphbox from "../../card/statbox/Graphbox";
import Statbox from "../../card/statbox/Statbox";
import Ad from "../../elements/ad/Ad";
import Listbox from "../../card/listbox/Listbox.js";

const Board = () => {
  // √âtat pour stocker le nom d'utilisateur r√©cup√©r√© du localStorage
  const [username, setUsername] = useState("");
  const [usermail, setUsermail] = useState("");
  const [datasurface, setDatasurface] = useState([]);
  const [datarecole, setDatarecolte] = useState([]);
  const [datanbterrain, setDatanbterrain] = useState([]);

  useEffect(() => {
    // R√©cup√©rer le nom d'utilisateur depuis le localStorage lors du montage du composant
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setUsername(storedUsername);
    }
    const storedUsermail = localStorage.getItem("usermail");
    if (storedUsermail) {
      setUsermail(storedUsermail);
    }

    // statistiques 1
    const userId = localStorage.getItem("userId");
    let req1 = "https://cropfarmback-production.up.railway.app/terrains/stats/";
    req1+=userId;
    xios.get(req1)
      .then(response => {
        // Mettez √† jour l'√©tat avec les donn√©es r√©cup√©r√©es depuis l'API
        setDatasurface(response.data);
        console.log(response.data);
    })
    .catch(error => {
      console.log('Erreur lors de la r√©cup√©ration des data surface :', error);
    });

    // statistiques2
    let req2 = "https://cropfarmback-production.up.railway.app/planters/recoltes/";
    req2+=userId;
    xios.get(req2)
      .then(response => {
        // Mettez √† jour l'√©tat avec les donn√©es r√©cup√©r√©es depuis l'API
        setDatarecolte(response.data);
        console.log(response.data);
    })
    .catch(error => {
      console.log('Erreur lors de la r√©cup√©ration des data surface :', error);
    });

    // statistiques3
    let req3 = "https://cropfarmback-production.up.railway.app/terrains/nombreterrains/";
    req3+=userId;
    xios.get(req3)
      .then(response => {
        // Mettez √† jour l'√©tat avec les donn√©es r√©cup√©r√©es depuis l'API
        setDatanbterrain(response.data);
        console.log(response.data);
    })
    .catch(error => {
      console.log('Erreur lors de la r√©cup√©ration des data surface :', error);
    });

    // fromage stat

  }, []);

  return (
    <div className="container">
      <div className="background">
        <div className="card-container">
          <Card_one titre="Surface parcelles cultivables en votre possession" info="55 m√®tre carr√©"/>
          <Card_two titre="Nombre de terrains en votre possession" info="5 Terrains"/>
          <Card_two titre="Nombre de plantes r√©colt√©es" info="44 Plantes"/>
        </div>
        <Ad />
        <div className="boxes">
          <Graphbox />
          <Statbox />
        </div>
        <Listbox />
      </div>

      <div className="greeting">
        {/* Utilisation du nom d'utilisateur r√©cup√©r√© dans le JSX */}
        <h1 className="title">Bonjour {username} üåæ</h1>
        <p className="subtitle">Bon retour sur votre interface administrateur</p>
      </div>
    </div>
  );
};

export default Board;
