// import React from "react";
import "./index.css"; // Importez votre fichier CSS
import React, { useEffect, useRef } from "react";
import Chart from "chart.js/auto"; // Importez Chart.js

const Graphbox = () => {
  const chartRef = useRef(null);

  useEffect(() => {
    // Données de l'évolution des récoltes mensuelles (à titre d'exemple)
    const monthlyHarvests = {
      labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"],
      datasets: [
        {
          label: "Récoltes mensuelles",
          data: [12, 19, 3, 5, 2, 3, 8, 13, 15, 10, 7, 22], // Exemple de données
          fill: false,
          borderColor: "rgb(75, 192, 192)",
          tension: 0.1,
        },
      ],
    };

    // Création du graphique
    const myChart = new Chart(chartRef.current, {
      type: "line",
      data: monthlyHarvests,
    });

    return () => {
      // Nettoyer le graphique lors du démontage du composant
      myChart.destroy();
    };
  }, []);

  return (
    <div className="float-bloc-stat">
      <div className="greet">
        <div className="title">Statistiques du mois</div>
        <div className="subtitle">Évolution des récoltes mensuelles</div>
      </div>
      <canvas ref={chartRef}></canvas>
    </div>
  );
};

export default Graphbox;
