// import React from "react";
import { Doughnut } from "react-chartjs-2"; // Importez le composant Doughnut de react-chartjs-2
import "./index.css";
import React, { useEffect, useRef } from "react";
// import Chart from "chart.js/auto"; // Importez votre fichier CSS

const Graphbox = ({ datasource }) => {
  // Données des statistiques des plantes
  useEffect(() => {
    if (!datasource || !Array.isArray(datasource) || datasource.length === 0) {
      return; // Ne rien faire si datasource est vide ou non défini
    }
    const labels = datasource.map(entry => entry.mois);

  }, [datasource]);

  const data = {
    labels: ["Fraise", "Banane", "Pomme", "Orange", "Citron"],
    datasets: [
      {
        label: "Statistiques des plantes",
        data: [10, 11, 20, 15, 12], // Exemple de données (en pourcentage)
        backgroundColor: [
          "#FF6384", // Couleur pour Fraise
          "#36A2EB", // Couleur pour Banane
          "#FFCE56", // Couleur pour Pomme
          "#4BC0C0", // Couleur pour Orange
          "#9966FF", // Couleur pour Citron
        ],
        hoverBackgroundColor: [
          "#FF6384",
          "#36A2EB",
          "#FFCE56",
          "#4BC0C0",
          "#9966FF",
        ],
      },
    ],
  };

  return (
    <div className="float-bloc-graph">
      <div className="greet">
        <div className="title">Statistiques du mois</div>
        <div className="subtitle">Statistiques des plantes utilisées/plantées</div>
      </div>
      <Doughnut data={data} />
    </div>
  );
};

export default Graphbox;
