import React, { useState, useEffect } from "react";
import axios from 'axios';
import { useLocation } from "react-router-dom";
import './index.css'; 
import Deletebutton from "../button/Deletebutton.js"
const ListInfo = ({idcategorie}) => {

  const location = useLocation();
  const [dataCategorie, setDataCategorie] = useState({});

  useEffect(() => {
    // const searchParams = new URLSearchParams(location.search);
    const idCategorie = idcategorie;

    axios.get(`https://cropfarmback-production.up.railway.app/plantes/categories/${idCategorie}`)
      .then(response => {
        setDataCategorie(response.data);
        console.log(response.data);
      })
      .catch(error => {
        console.log('Erreur lors de la récupération des catégories de plantes :', error);
      });
  }, [location.search]);


  return (
    <div className="float-bloc-list">
    <div className="greet" >
        <div className="title">Liste des plantes de la categorie</div>
        <div className="subtitle">Les belles plantes ^^</div>
    </div>

        <br></br>
        <div className="list-info">
        <div className="plant-image">
            <img src="/assets/plant.jpg" alt="Plant" />
        </div>
        {dataCategorie.data?.map(plante => (

          <div className="plant-name" style={{ width: '50%', marginTop: '0%' }}>{plante.nomplante}</div>

        ))}
        {/* <Deletebutton/> */}
        </div>

    </div>
  );
};

export default ListInfo;
